Trigger.script:
svo.parsing_vlist = line:find("Venom") and true or false

svo.es_capture()

svo.storedvials = {}

if not string.find(getLines(getLineNumber()-1, getLineNumber())[1], "^Vial") then setTriggerStayOpen("svo Capture elist/elist2/venomlist", 0)
svo.es_captured(svo.parsing_vlist) send("config pagelength "..(svo.conf.pagelength >= 20 and svo.conf.pagelength or 20), false)
svo.gag_list = nil end

ScriptGroup.script:
function elistPrio(_, module)
  if module ~= "svo (elistsorter)" then return true end
  tempTimer(0, [[setModulePriority("]]..module..[[", 1)]])
end

registerAnonymousEventHandler("sysInstall", "elistPrio", true)

svo = svo or {}
svo.modules_version = svo.modules_version or {}
svo.modules_version["svo (elistsorter)"] = 1.1
