function svo.showupdatereminder(newversion)
  svo.announceupdates = nil

  svo.updatelabel = svo.updatelabel or Geyser.Label:new({
    name = "svo.updatelabel",
    x = "-340px", y = "-115px",
    width = "320px", height = "110px",
  })

  svo.doupdatelabel = svo.doupdatelabel or Geyser.Label:new({
    name = "svo.doupdatelabel",
    x = "25%", y = "-30px",
    width = "20%", height = "20px",
  }, svo.updatelabel)

  svo.dontupdatelabel = svo.dontupdatelabel or Geyser.Label:new({
    name = "svo.dontupdatelabel",
    x = "55%", y = "-30px",
    width = "20%", height = "20px",
  }, svo.updatelabel)

  -- grey to black shade, top to bottom
  svo.updatelabel:setStyleSheet([[
      margin: 0px;
      padding: 2px;

      /* Vertical gradient */
      background: qlineargradient(
          x1: 0, y1: 0, x2: 0, y2: 1,
          stop: 0 #3c3c3c, stop: 1 #232323
      );

      border: none;
      border-radius: 4px;

      color: #ffffff;

    qproperty-alignment: 'AlignTop | AlignHCenter';
    qproperty-wordWrap: true;
    font-family: 'Ubuntu','Calibri',serif;
  ]])

  svo.doupdatelabel:setStyleSheet([[
    border-radius: 4px;
    border-style: double;
    border-width: 2px;
    border-color: green;
    font-family: monospace;
  ]])

  svo.dontupdatelabel:setStyleSheet([[
    border-radius: 4px;
    border-style: double;
    border-width: 2px;
    border-color: grey;
    font-family: monospace;
  ]])

  svo.updatelabel:echo(string.format([[<p align="center" style="font-size:10pt; color:white">Hey, a new Svo (%s) has been downloaded and is available for install.<br><br>Install and restart now, or install later?<p>]], newversion))

  svo.doupdatelabel:echo([[<p align="center" style="font-size:8pt; color:white">Restart</p]])

  svo.dontupdatelabel:echo([[<p align="center" style="font-size:8pt; color:white">Later</p]])

  svo.updatelabel:show()
  svo.doupdatelabel:show()
  svo.dontupdatelabel:show()

  svo.doupdatelabel:setClickCallback("svo_doupdate_click")
  svo.dontupdatelabel:setClickCallback("svo_dontupdate_click")
end

function svo_dontupdate_click()
  svo.updatelabel:hide()
  svo.doupdatelabel:hide()
  svo.dontupdatelabel:hide()
end

function svo_doupdate_click()
  svo.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">Updating Svo...<p>]])
  svo.doupdatelabel:hide()
  svo.dontupdatelabel:hide()

  local name = svo.me.name

  if not (uninstallPackage and installPackage) then
    svo.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">Can't update your Svo. You didn't update to Mudlet 2.1, and your current Mudlet lacks the features necessary. Go update first please.<p>]])
    return
  end

  if not io.exists(getMudletHomeDir().."/svo/downloads/"..string.format("%s svo.zip", svo.me.name)) then
    svo.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">Hm, can't update Svo - seems the new system we downloaded dissapeared. Do 'vupdate force' to download it again.<p>]])
    return
  end

  function svo_update_system()
    svo_updating_system = "uninstalling"
    uninstallPackage(name.." svo")

    svo_updating_system = "installing"
    svo.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">Uninstalled the old Svo, installing the new one now...<p>]])
    installPackage(getMudletHomeDir().."/svo/downloads/"..string.format("%s svo.zip", svo.me.name))

    svo_updating_system = nil
    svo.echof("Svo updated! Restart Mudlet, and you'll be all done. You can find the changelog on svo.vadisystems.com.")

    svo.updatelabel:echo([[<p align="center" style="font-size:10pt; color:white">Svo updated!<br><br>Restart Mudlet, and you'll be all done.<p>]])
    svo.updatelabel:setStyleSheet([[
        margin: 0px;
        padding: 2px;

        /* Vertical gradient */
        background: qlineargradient(
            x1: 0, y1: 0, x2: 0, y2: 1,
            stop: 0 #3c3c3c, stop: 1 #232323
        );

        border: none;
        border-radius: 4px;

        color: #ffffff;

      qproperty-alignment: 'AlignVCenter | AlignHCenter';
      qproperty-wordWrap: true;
      font-family: 'Ubuntu','Calibri',serif;
    ]])
  end

  svo_updating_system = "started"
  tempTimer(10, function()
    if not svo_updating_system then return end

    if svo_updating_system == "uninstalling" then
      svo.updatelabel:echo([[<p align="center" style="font-size:8pt; color:white">Hm, seems the update got stuck on the uninstallPackage() function. Try uninstalling Svo from the Package Manager and then installing it from the zip (found in ]].. getMudletHomeDir().."/svo/downloads/"..string.format("%s svo.zip", svo.me.name) ..[[) to upgrade now.<p>]])
    elseif svo_updating_system == "installing" then
      svo.updatelabel:echo([[<p align="center" style="font-size:8pt; color:white">Hm, seems the update got stuck on the installPackage() function. Old Svo is uninstalled, but it the new one didn't get installed - install your Svo zip (found in ]].. getMudletHomeDir().."/svo/downloads/"..string.format("%s svo.zip", svo.me.name) ..[[ via the Package Manager.<p>]])
    end
  end)
  svo_update_system()
end

-- just shows how to suffix affs to the prompt so you know

function mm_display_affs()
	-- put two dashes before the line below so this function will work!
	if true then return end

	-- this if/elseif chain will append one aff at a time only
	if svo.affl.prone then
		echo("[[PRONE]] ")
	elseif svo.affl.paralysis then
		echo("[[PARALYSIS]] ")
	elseif svo.affl.crippledleftleg then
		echo("[[CRIPPLED LEFTLEG]] ")
	end

	-- these stand-alone ifs will allow for several affs to be suffixed
	if svo.affl.crippledrightleg then
		echo("[[CRIPPLED RIGHT LEG]] ")
	end

	if svo.affl.tangled then
		echo("[[TANGLED]] ")
	end
end

-- magical chat capture that doesn't require any triggers!
-- made for Svo
svo.ui = svo.ui or {}

function svo_captureChats()
    if not svo.ui.chat then return end

    tempLineTrigger(0,1,[[
        if isPrompt() then return end
		selectCurrentLine()
		copy()

		if string.starts(line, "(Party)") then svo.ui.chat:Append("Party")
		else svo.ui.chat:Append("All") end
]])
end
