Script.script:
function svo.add_peopletracker_config() svo.me.watchfor = svo.me.watchfor or {}
svo.config.setoption("watchfor", { type = "string", check = function(what) if what:find("^%w+$") then return true end end, onset = function () local name = string.title(svo.conf.watchfor) -- we want nil, not false so 'or' doesn't help if svo.me.watchfor[name] then svo.me.watchfor[name] = nil else svo.me.watchfor[name] = true end
if svo.me.watchfor[name] then svo.echof("Added <0,255,0>%s%s to the watchfor list - use qwhow to see them.", name, svo.getDefaultColor()) else svo.echof("Removed %s from the watchfor list.", name) end raiseEvent("svo watchfor name changed", name) end }) end
