Trigger.script:
if not svo.putvials then return end

svo.putvials = nil
echo'\n' svo.echof("Done stuffing away vials - they can give theirs now.")

if not svo.getvials then return end

svo.getvials = nil
echo'\n' svo.echof("Retrieved all of your vials back.")

Alias.script:
svo.givevials = {person = matches[2]}
if svo.defkeepup[svo.defs.mode].selfishness then svo.defs.keepup("selfishness", false) svo.givevials.selfish = true end
svo.doadd("give 50 vial to "..svo.givevials.person)

ScriptGroup.script:
function refillerPrio(_, module)
  if module ~= "svo (refiller)" then return true end
  tempTimer(0, [[setModulePriority("]]..module..[[", 0)]])
end

registerAnonymousEventHandler("sysInstall", "refillerPrio", true)
svo = svo or {}; svo.loader = svo.loader or {}
svo.modules_version = svo.modules_version or {}
svo.modules_version["svo (refiller)"] = 1
